#include <iostream>
#include <string>
#include <utility>

using namespace std;

class Solution {
public:
  pair<int, int> getPalindromePair(const string &s, int center1, int center2) {
    int i = center1;
    int j = center2;
    pair<int, int> index_pair(center2, center2);

    while (i >= 0 && j < s.size() && s[i] == s[j]) {
      index_pair.first = i;
      index_pair.second = j;
      i--;
      j++;
    }

    return index_pair;
  }

  string shortestPalindrome(string s) {
    if (s.size() <= 1) {
      return s;
    }

    int max_end = 0;
    for (int i = 1; i <= s.size() / 2; i++) {
      pair<int, int> index_pair = getPalindromePair(s, i, i);
      if (index_pair.first == 0 && index_pair.second > max_end) {
        max_end = index_pair.second;
      }
      index_pair = getPalindromePair(s, i - 1, i);
      if (index_pair.first == 0 && index_pair.second > max_end) {
        max_end = index_pair.second;
      }

    }

    string result;
    for (int i = s.size() - 1; i > max_end; i--) {
      result += s[i];
    }
    for (int i = 0; i < s.size(); i++) {
      result += s[i];
    }

    return result;
  }
};

int main(void) {
  Solution sol;
  string result = sol.shortestPalindrome("aacecaaa");
  cout << result << endl;
  result = sol.shortestPalindrome("abcd");
  cout << result << endl;
  result = sol.shortestPalindrome("aaaa");
  cout << result << endl;
  result = sol.shortestPalindrome("");
  cout << result << endl;
  result = sol.shortestPalindrome("a");
  cout << result << endl;
  result = sol.shortestPalindrome("aaacdc");
  cout << result << endl;
  result = sol.shortestPalindrome("ab");
  cout << result << endl;
  result = sol.shortestPalindrome("");
  cout << result << endl;

}
